let myButton=document.getElementById("scrollTop");function scrollFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?myButton.style.display="block":myButton.style.display="none"}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0,history.pushState(null,"","")}window.onscroll=function(){scrollFunction()},myButton.addEventListener("click",topFunction);